import React from 'react';
import trending from '../assets/trending.js';
import PostMasonry from '../components/common/post-masonry';
import MasonryPost from '../components/common/masonry-post';
import featured from '../assets/featured.js';
import PostGrid from '../components/common/post-grid';

const trendingConfig = {
1: {
gridArea: '1 / 2 / 3 / 3'
     }
};
const featuredConfig = {
0:{
	gridArea: '1 / 1 / 2 / 3',
	height: '300px'
},
2:{
	height: '300px'
},
3:{
	height: '630px',
	marginLeft: '30px',
	width: '630px'
}
}
const mergeStyles = function (posts, config) {
posts.forEach((post, index)=> {
		post.style = config[index];
		post.author = 'Miss Mallo'
		post.description = "Is life forward or is it backwards? Tune it to view my views on life, let's go deeper into the way it is and the way it's supposed to be"
	}
)
};

const recentPosts = [...trending, ...featured, ...featured, ...trending]

mergeStyles(trending, trendingConfig);
mergeStyles(featured, featuredConfig);

const lastFeatured = featured.pop();

export default function Home() {
	return (
<main className="home">
 <section className="container">

  <div className="row">
     
       <section className= "featured-posts-container">
         <PostMasonry posts={featured} columns={2} tagsOnTop= {true} />
         <MasonryPost post={lastFeatured} tagsOnTop={true} />
       </section>
    </div>
 </section>
<section className="bg-white">
 <section className="container">

  <div className="row">
  <h1>Recent Posts</h1>
   <PostGrid posts= {recentPosts} />
    </div>
 </section>
</section>
 <section className="container">

  <div className="row">
     
   <PostMasonry posts={trending} columns={3} />
    </div>
 </section>
  
</main>
); 

}
