import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {Avatar} from 'antd';
 const navLinks = [


{
	title: 'Blog', 
	path: '/home'
},
{
	title: 'Web Dev', 
	path: '/web-dev'
},
{
	title: 'Cloud', 
	path: '/cloud'
},
{
	title: 'Algorithm', 
	path: '/algorithm'
},
{
	title: 'Login', 
	path: '/login'
}

 ];
export default function Navigation({user}) {
	const [menuActive, setMenuActive] = useState(false)
return (<nav className="site-navigation" role="navigation">
	<span className="menu-title">Vee's Blog</span>
	<div className={`menu-content-container ${menuActive && 'active'}`}>
	<ul>
 { 
 navLinks.map((link, index)=> {return <li key= {index}><Link to={link.path}>{link.title}</Link></li>})
}
	</ul>
	<span className="menu-avatar-container"><Avatar src="./images(14).jpg" size="32" /></span><span className="menu-avatar-container">{`${user.firstName} ${user.lastName}`}</span>
	
	</div>
<i className="ionicons icon ion-ios-menu" onClick={()=> setMenuActive(!menuActive)} />
	</nav>);
};
