import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {Avatar} from 'antd';
 const navLinks = [


{
	title: 'Home' , 
	path: '/'
},
{
	title: 'Blog', 
	path: '/blog'
},
{
	title: 'Contact Us', 
	path: '/contact-us'
},
{
	title: 'Login', 
	path: '/login'
}
 ];
export default function Navigation({user}) {
	const [menuActive, setMenuActive] = useState(false)
return (<nav className ="site-navigation">
	<span className="menu-title">Vees Blog</span>
	<div className="menu-content-container">
	<ul>
 { 
 navLinks.map((link, index)=> {return <li key= {index}><Link to={link.path}>{link.title}</Link></li>})
}
	</ul>
	<span><Avatar src="./images(14).jpg" size="32" /></span><span className="menu-avatar-container">{`${user.firstName} ${user.lastName}`}</span>
	<i className="ionicons icon ion-ios-menu" onClick={()=> setMenuActive(!menuActive)} />
	</div>

	</nav>);
};
