import React from 'react';
import trending from '../assets/trending.js';
import PostMasonry from '../components/common/post-masonry';
import MasonryPost from '../components/common/masonry-post';
import feature from '../assets/feature.js';

const trendingConfig = {
1: {
gridArea: '1 / 2 / 3 / 3'
     }
};
const featureConfig = {

}
const mergeStyles = function (posts, config) {
posts.forEach((post, index)=> {
		post.style = config[index];
	}
)
};
const lastPost = feature.pop();

mergeStyles(trending, trendingConfig);
mergeStyles(feature, featureConfig);
export default function Home() {
	return (<section className="container home">
  <div className="row">
  <h1>Featured Post</h1>
  <section className= "feature-posts-container">
   <PostMasonry posts={feature} columns={2} tagsOnTop= {true} />
   <MasonryPost posts={lastPost} tagsOnTop= {true} />
   </section>
   <h1>Trending Posts</h1>
   <PostMasonry posts={trending} columns={3} />
  
  </div>
</section>); 

}
