import React from 'react';
import {categoryColors} from './styles';

export default function MasonryPost({post, tagsOnTop}) {
const windowWidth = window.innerWidth
const imageBackground = {backgroundImage: `url("${require(`../../assets/images/${post.image}`)}")`};

const style = windowWidth > 900 ?  {...imageBackground, ...post.style} : imageBackground ;

return (
<a className="masonry-post overlay" href={post.link} style={style}>
<div className="image-test">
<div className="tags-container">
{post.categories.map((tag, ind)=> {
return <span key={ind} className="tag" style={{backgroundColor: categoryColors[tag]}}>
{tag.toUpperCase()}
</span>
})}
</div>
<div>
<h2 className="image-title">{post.title}</h2>
<span className="image-date">{post.date}</span>
</div>
</div>
</a>
	);
}
